    private void jbutton6ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbutton6ActionPerformed
        jbutton6.setBackground(Color.red);
        jbutton6.setForeground(Color.PINK);
        DefaultTableModel dt=(DefaultTableModel)jtabel.getModel();
        dt.setRowCount(0);
        try {
            Class.forName("com.mysql.cj.jdbc.Driver");
            Connection cot = DriverManager.getConnection("jdbc:mysql://localhost:3306/project", "root", "");
            //Statement st=(Statement) cot.createStatement();
            Statement st = cot.createStatement();
            ResultSet rs = st.executeQuery("Select * from block3");
            while (rs.next()) {
                int number = rs.getInt("number");
                String activate = rs.getString("activate");
                String roomstatus = rs.getString("roomstatus");
                Object[] tbdata = {number, activate, roomstatus};

                dt.addRow(tbdata);

            }

        } catch (ClassNotFoundException | SQLException ex) {
            Logger.getLogger(block1frame.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_jbutton6ActionPerformed

